import React, { useEffect, useRef } from "react";
import Img1 from "../assests/img1.png";
import Img2 from "../assests/img2.png";
import Img3 from "../assests/img3.png";
import Img4 from "../assests/img4.png";
import Logo from "../assests/logo.svg";
function Nav() {
    const [scrolled, setScrolled] = React.useState(false);
    const [dropDown, setDropDown] = React.useState(false);
    const NavLi = useRef();
    const handleScroll = () => {
        const offset = window.scrollY;
        if (offset > 10) {
            setScrolled(true);
            console.log(scrolled);
        } else {
            setScrolled(false);
        }
    };
    const dropDownBox = () => {
        let Drop = NavLi.current.children.drop_features;
        if (dropDown == false) {
            Drop.style.display = "flex";
            setDropDown(true);
        } else if (dropDown == true) {
            Drop.style.display = "none";
            setDropDown(false);
        }
    };

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
    });
    return (
        <>
            <nav className={scrolled ? "sticky scroll" : "sticky"}>
                <div className="logo">
                    <img width="40px" src={Logo} alt="" />
                </div>
                <div className="nav_links">
                    <ul>
                        <li onClick={dropDownBox}>Features</li>
                        <li>Developer</li>
                        <li>About</li>
                        <li>News</li>
                        <li>Resources </li>
                    </ul>
                </div>
                <div className="nav-btn">
                    <a>BuySycoin</a>
                    <span>&#9654;</span>
                </div>
                <div className="nav-btn">
                    <a>Join Discord</a>
                    <span>&#9654;</span>
                </div>
                <div className="drop-down" ref={NavLi}>
                    <div
                        className="drop-cat"
                        id="drop_features"
                        style={{ display: "none" }}
                    >
                        <div className="drop-img">
                            <img src={Img1} alt="" />
                            <p>Mater</p>
                        </div>
                        <div className="drop-img">
                            <img src={Img2} alt="" />
                            <p>Mater</p>
                        </div>
                        <div className="drop-img">
                            <img src={Img3} alt="" />
                            <p>Mater</p>
                        </div>
                        <div className="drop-img">
                            <img src={Img4} alt="" />
                            <p>Mater</p>
                        </div>
                    </div>
                </div>
            </nav>
        </>
    );
}
export default Nav;
