import React, { useState, useRef, useEffect } from "react";
import useWebAnimations, {
    backInLeft,
    backInRight,
} from "@wellyshen/use-web-animations";

import Logo from "./assests/logo.svg";
import "./App.css";
import Orange from "./assests/Orange.svg";
function Tiny() {
    const { ref, playState, getAnimation } = useWebAnimations({
        ...backInLeft,
        timing: {
            duration: 2000,
            // iterations: Infinity,
        },
    });

    const [isSticky, setSticky] = useState(false);
    const [scrolled, setScrolled] = React.useState(false);

    const handleScroll = () => {
        const offset = window.scrollY;
        if (offset > 10) {
            setScrolled(true);
            console.log(scrolled);
        } else {
            setScrolled(false);
        }
    };

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
    });
    return (
        <>
            <div className="background bg2">
                <nav className={scrolled ? "sticky scroll" : "sticky"}>
                    <div className="logo">
                        <img width="40px" src={Logo} alt="" />
                    </div>
                    <div className="nav_links">
                        <ul>
                            <li>Features</li>
                            <li>Developer</li>
                            <li>About</li>
                            <li>News</li>
                            <li>Resources </li>
                        </ul>
                    </div>
                    <div className="nav-btn">
                        <a>BuySycoin</a>
                        <span>&#9654;</span>
                    </div>
                    <div className="nav-btn">
                        <a>Join Discord</a>
                        <span>&#9654;</span>
                    </div>
                </nav>
                <br />
                <br />
                <br />

                <img ref={ref1} className="hr-line" src={Orange} alt="" />
                <div ref={ref} className="Shell">
                    <h1>
                        The blockchain platform for real money, assets, <br />
                        and blazing-fast token payments that scale. {playState}
                    </h1>
                    <br />
                    <br />
                    <br />
                    <br />

                    <h3>
                        Syscoin Platform provides trustless interoperability
                        with Ethereum ERC-20, token & <br /> asset
                        microtransactions, and Bitcoin-core-compliant
                        merge-mined security.
                    </h3>
                    <br />
                    <br />
                    <br />
                    <br />
                    <div className="shell-btns">
                        <button>Support</button>
                        <button>Wallets</button>
                        <button>News</button>
                        <button>White Papers</button>
                        <button>Roadmap</button>
                    </div>
                </div>
            </div>
        </>
    );
}
export default Tiny;
